import { useState } from 'react'

const App = (props) => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' }
  ]) 

  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filterName, setFilterName] = useState('')

  return (
    <div>
      <h2>Phonebook</h2>
      <div>
        filter shown with: <input onChange={event => setFilterName(event.target.value)}/>
      </div>
      <h2>Add a new number</h2>
      <form onSubmit={event => addNote(persons={persons},newName={newName},newNumber={newNumber}, setPersons={setPersons})}>
        <div>
          name: <input onChange={event => setNewName(event.target.value)} />
        </div>
        <div>number: <input onChange={event => setNewNumber(event.target.value)}/></div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {<GetEm names={[persons,setPersons]} filter={[filterName, setFilterName]}/>}
    </div>
  )
}

function alreadyExists(array,name) {
  return array.some(function(check) {
    return check.name === name;
  }); 
}

const addNote = (event,props) =>{
  const persons = props.persons
  const newName = props.newName
  const newNumber = props.newNumber
  const setPersons = props.setPersons
  event.preventDefault()

  if (alreadyExists(persons,newName)){
    window.alert(`${newName} is already added to phonebook`);
  }

  else{
    setPersons([...persons,{name:newName,number:newNumber}])
  }    
}

function filterPhoneBook(array, string) {
  return array.filter(v =>
      Object.keys(v).some(k => v[k].toLowerCase().includes(string.toLowerCase())));
}

const GetEm = (props) =>{
  const lista = props.names[0]
  const filter = props.filter[0]

  if (filter.length <1){
    return(
      <div>
          {lista.map(nimi =>{
            return(
              <div key={nimi.name}>
                <p>{nimi.name} {nimi.number}</p>
              </div>
            )
          })}  
      </div>
    )
  }

  else {
    const cur = filterPhoneBook(lista,filter);
    return(
      <div>
          {cur.map(nimi =>{
            return(
              <div key={nimi.name}>
                <p>{nimi.name} {nimi.number}</p>
              </div>
            )
          })}  
      </div>
    )
  }
}



export default App
