import { useState } from 'react'

const App = (props) => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' }
  ]) 

  const [filterName, setFilterName] = useState('')

  function alreadyExists(name) {
    return persons.some(function(check) {
      return check.name === name;
    }); 
  }

  function filterByValue(array, string) {
    return array.filter(o =>
        Object.keys(o).some(k => o[k].toLowerCase().includes(string.toLowerCase())));
}

  const addNote = (event) =>{
    const addedName = event.target.elements.newname.value
    event.preventDefault()

    if (alreadyExists(addedName)){
      window.alert(`${addedName} is already added to phonebook`);
    }

    else{
      setPersons([...persons,{name:event.target.elements.newname.value,number:event.target.elements.number.value}])
    }    
  }

  const GetEm = (props) =>{
    const lista = props.names[0]
    const filter = props.filter[0]


    if (filter.length <1){
      return(
        <div>
            {lista.map(nimi =>{
              return(
                <div key={nimi.name}>
                  <p>{nimi.name} {nimi.number}</p>
                </div>
              )
            })}  
        </div>
      )
    }

    else {
      const cur = filterByValue(lista,filter);
      return(
        <div>
            {lista.map(nimi =>{
              return(
                <div key={nimi.name}>
                  <p>{nimi.name} {nimi.number}</p>
                </div>
              )
            })}  
        </div>
      )
    }
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <div>
        filter shown with: <input onChange={event => setFilterName(event.target.value)}/>
      </div>
      <h2>Add a new number</h2>
      <form onSubmit={addNote}>
        <div>
          name: <input type='text' name='newname' />
        </div>
        <div>number: <input name='number'/></div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {<GetEm names={[persons,setPersons]} filter={[filterName, setFilterName]}/>}
    </div>
  )

}

export default App
